package com.goodjob.apigateway.application.config;import com.goodjob.apigateway.infrastructure.security.filter.JwtAuthenticationFilter;import lombok.RequiredArgsConstructor;import org.springframework.cloud.gateway.route.RouteLocator;import org.springframework.cloud.gateway.route.builder.RouteLocatorBuilder;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configuration@RequiredArgsConstructorpublic class GatewayConfig {  private final JwtAuthenticationFilter jwtAuthenticationFilter;  @Bean  public RouteLocator customRouteLocator(RouteLocatorBuilder builder) {    return builder.routes()            .route("metadata-service", r -> r.path("/api/v1/metadata/**")                    .filters(f -> f.filter(jwtAuthenticationFilter))                    .uri("lb://metadata-service"))            .route("profile-service", r -> r.path("/api/v1/profiles/**")                    .filters(f -> f.filter(jwtAuthenticationFilter))                    .uri("lb://profile-service"))            .route("company-service", r -> r.path("/api/v1/companies/**")                    .filters(f -> f.filter(jwtAuthenticationFilter))                    .uri("lb://company-service"))            .route("job-service", r -> r.path("/api/v1/jobs/**")                    .filters(f -> f.filter(jwtAuthenticationFilter))                    .uri("lb://job-service"))            .route("mail-service", r -> r.path("/api/v1/mails/**")                    .filters(f -> f.filter(jwtAuthenticationFilter))                    .uri("lb://mail-service"))            .route("notification-service", r -> r.path("/api/v1/notifications/**")                    .filters(f -> f.filter(jwtAuthenticationFilter))                    .uri("lb://notifications-service"))            .build();  }}